import PySimpleGUI as sg
import board_utils as butil
import analysis

button_right_base64 = b''
button_left_base64 = b''

starting_pos_fen = "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"
random_fen = "1n2k3/r1p1p1b1/p3q3/1P1r1np1/p5RP/R2Pp3/1Bb2P2/1N1QKBN1 w - - 3 22"


bar_val = 0


def run():
	board = [
		[
			sg.Graph(canvas_size=(800, 800),
					graph_bottom_left=(0,0),
					graph_top_right=(800,800),
					key="board"
			)
		]
	]
	printout = [
		[sg.Output(size=(100,20))],
	]
	analysis_bar = [
		[
			sg.Graph(canvas_size=(35, 800),
				graph_bottom_left=(0,0),
				graph_top_right=(35,800),
				key="analysis_bar"
			)
		]
	]

	layout = [
		[sg.Column(analysis_bar),
		sg.VSeparator(),
		sg.Column(board),
		sg.VSeparator(),
		sg.Column(printout)],
		[sg.Button('', key='Back', image_data=button_left_base64),
		sg.Button('', key='Next', image_data=button_right_base64)],
	]
	window = sg.Window('Match Analyzer', layout)

	bar = window.Element('analysis_bar')
	board_graph = window.Element('board')
	window.Finalize()

	butil.Init(board_graph, bar)

	#fen_data = butil.data_from_fen(starting_pos_fen)
	#butil.draw_from_data(board_graph, fen_data)
	butil.set_pos_from_fen(random_fen)



	while True:
		event, values = window.read()
		global bar_val

		if event == 'Next':
			pass
		elif event == 'Back':
			bar_val = bar_val - 0.5
			print(bar_val)
			analysis.move_bar(bar_val)
		elif event == sg.WIN_CLOSED:
			break

	analysis.close()
	window.close()

if __name__ == '__main__':
	run()